import React from 'react';
import { Uploady, useDropzone } from '@rpldy/uploady';

const MyUploadyDropzone = () => {
  const { getRootProps, getInputProps } = useDropzone({
    onDrop: (files) => {
      console.log(files);
    },
  });

  return (
    <Uploady>
      <div {...getRootProps()} style={{ border: '2px dashed #007bff', padding: '20px', textAlign: 'center' }}>
        <input {...getInputProps()} />
        <p>파일을 드래그하거나 클릭하여 업로드하세요.</p>
      </div>
    </Uploady>
  );
};

export default MyUploadyDropzone;
