import React from "react";
import Header from "./Header";
import "./css/Page2.css";

function Page2({ location }) {
    const { formData, works, workFiles } = location.state || { formData: {}, works: [], workFiles: [] };

    return (
        <div>
            <Header />
            <section>
                <h2>출품자 정보</h2>
                <p>성함: {formData.name}</p>
                <p>아이디: {formData.id}</p>
                <p>연락처: {formData.contact}</p>

                <h2>작품 정보</h2>
                {works.map((work, index) => (
                    <div key={index}>
                        <h3>작품 {index + 1}</h3>
                        <p>작품 제목: {work.title}</p>
                        <p>작품 내용: {work.description}</p>
                        <p>감독명: {work.director}</p>
                        <p>배우명: {work.actors}</p>
                        <p>추가입력사항: {work.additionalInfo}</p>
                        {workFiles[index]?.videoUrl && (
                            <div>
                                <p>작품 영상 미리보기:</p>
                                <video controls width="300">
                                    <source src={workFiles[index].videoUrl} type="video/mp4" />
                                    Your browser does not support the video tag.
                                </video>
                            </div>
                        )}
                        {workFiles[index]?.thumbnailUrl && (
                            <div>
                                <p>작품 썸네일 미리보기:</p>
                                <img src={workFiles[index].thumbnailUrl} alt="Thumbnail" width="100" />
                            </div>
                        )}
                    </div>
                ))}
            </section>
        </div>
    );
}

export default Page2;
