import React, { useState } from 'react';
import Header from "./Header";
import "./css/Page1.css";
import circle1 from '../src/image/page1.png';
import DropFileInput from "./components/drop-file-input/DropFileInput";
import ImgDrop from "./components/drop-file-input/ImgDrop";
import plus from "./image/plus.png";
import PlusPage from './components/PlusPage';
import next from "./image/right.png";
import { Link } from 'react-router-dom';

function Page1() {
    // 작품 1, 2, 3을 위한 상태 배열
    const [works, setWorks] = useState([{}, {}, {}]); // 작품 1, 2, 3에 대한 빈 객체 초기화
    const [plusPages, setPlusPages] = useState([[], []]); // 작품 2와 3에 대한 PlusPage 배열

    // PlusPage 삭제 함수
    const handleRemovePlusPage = (workIndex, pageIndex) => {
        const newPlusPages = [...plusPages];
        newPlusPages[workIndex] = newPlusPages[workIndex].filter((_, i) => i !== pageIndex);
        setPlusPages(newPlusPages);
    };

    // PlusPage 추가 함수
    const handleAddPlusPage = (workIndex) => {
        const newPlusPages = [...plusPages];
        if (newPlusPages[workIndex].length < 3) { // 최대 3개까지 추가
            newPlusPages[workIndex].push(<PlusPage key={newPlusPages[workIndex].length} onRemove={() => handleRemovePlusPage(workIndex, newPlusPages[workIndex].length)} />);
            setPlusPages(newPlusPages);
        }
    };

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle1} className="circle1" alt="circle1"></img>
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr></hr>
            <section>
                <p className="info">1.출품자 정보</p>
                <ul className="box box1">
                    <li className="first">성함<span className="red">*</span></li>
                    <li className="second"><input type="text" placeholder="POBA누리 연동 자동 입력"></input></li>
                </ul>
                <ul className="box box2">
                    <li className="first">아이디<span className="red">*</span></li>
                    <li className="second"><input type="text" placeholder="POBA누리 연동 자동 입력"></input></li>
                </ul>
                <ul className="box box3">
                    <li className="first">연락처<span className="red">*</span></li>
                    <li className="second"><input type="text" placeholder="POBA누리 연동 자동 입력"></input></li>
                </ul>

                <p className="info">2.작품정보 <span className="three">(최대 3개까지 출품가능합니다)</span></p>
                <ul className="box box1">
                    <li className="first">작품제목<span className="red">*</span></li>
                    <li className="second"><input type="text" className="movieTitle" placeholder="작품제목을 입력해주세요."></input></li>
                </ul>
                <ul className="box box4">
                    <li className="first">작품내용<span className="red">*</span></li>
                    <li className="second"><textarea placeholder="작품내용을 입력해주세요"></textarea></li>
                </ul>
                <ul className="box box1">
                    <li className="first">감독명<span className="red">*</span></li>
                    <li className="second"><input type="text" className="movieTitle" placeholder="감독 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)"></input></li>
                </ul>
                <ul className="box box1">
                    <li className="first">배우명</li>
                    <li className="second"><input type="text" className="movieTitle" placeholder="배우 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)."></input></li>
                </ul>
                <ul className="box box1">
                    <li className="first">추가입력사항</li>
                    <li className="second"><input type="text" className="movieTitle" placeholder="예) 촬영,편집 등"></input></li>
                </ul>
                <ul className="box drop">
                    <li className="first">작품 영상 첨부<span className="red">*</span></li>
                    <li className="drop"><DropFileInput /></li>
                </ul>
                <ul className="box drop">
                    <li className="first">작품 썸네일 첨부</li>
                    <li className="drop"><ImgDrop /></li>
                </ul>
                <p className="imgText">※ 썸네일이란, 영상을 클릭하기 전에 내용을 미리 보여주는 작은 대표 이미지입니다.</p>

                {/* 추가된 PlusPage 컴포넌트들 */}
                {plusPages.map((pages, workIndex) => (
                    <div key={workIndex}>
                        {pages.map((page, pageIndex) => (
                            <div key={pageIndex}>
                                {page}
                            </div>
                        ))}
                        {pages.length < 3 && (
                            <button className="plus" onClick={() => handleAddPlusPage(workIndex)}>
                                <img src={plus} alt="plus"></img>작품 추가하기
                            </button>
                        )}
                    </div>
                ))}
            </section>

            <Link to={"/page2"}>
                <div className='next'>
                    다음 <img src={next}></img>
                </div>
            </Link>
        </div>
    );
}

export default Page1;
