import React, { useState } from 'react';
import Header from "./Header";
import "./css/Page1.css";
import circle1 from '../src/image/page1.png';
import DropFileInput from "./components/drop-file-input/DropFileInput";
import ImgDrop from "./components/drop-file-input/ImgDrop";
import plus from "./image/plus.png";
import PlusPage from './components/PlusPage';
import next from "./image/right.png";
import { Link } from 'react-router-dom';

function Page1() {
    const [plusPages, setPlusPages] = useState([]); 
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        director: '',
        actors: '',
        additional: '',
    });

    // PlusPage 삭제 함수
    const handleRemovePlusPage = (index) => {
        setPlusPages(plusPages.filter((_, i) => i !== index));
    };

    // PlusPage 추가 함수
    const handleAddPlusPage = () => {
        if (plusPages.length < 2) { 
            setPlusPages([...plusPages, <PlusPage key={plusPages.length} onRemove={() => handleRemovePlusPage(plusPages.length)} />]);
        }
    };

    // 입력값 변경 함수
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle1} className="circle1" alt="circle1"></img>
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr></hr>
            <section>
                <p className="info">1.출품자 정보</p>
                {/* ... 여기에 입력 필드 추가 ... */}
                <ul className="box box1">
                    <li className="first">작품제목<span className="red">*</span></li>
                    <li className="second">
                        <input
                            type="text"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            placeholder="작품제목을 입력해주세요."
                        />
                    </li>
                </ul>
                {/* 나머지 입력 필드도 비슷하게 수정 */}

                {/* 추가된 PlusPage 컴포넌트들 */}
                {plusPages.map((page, index) => (
                    <PlusPage key={index} onRemove={() => handleRemovePlusPage(index)} />
                ))}
            </section>

            {plusPages.length < 2 && (
                <button className="plus" onClick={handleAddPlusPage}>
                    <img src={plus} alt="plus"></img>작품 추가하기
                </button>
            )}
            <Link to={"/page2"} state={{ formData, plusPages }}>
                <div className='next'>
                    다음 <img src={next} alt="next" />
                </div>
            </Link>
        </div>
    );
}

export default Page1;
