import React from 'react';
import { Uploady, useFileInput } from '@rpldy/uploady';

const MyUploadyDropzone = () => {
  // useFileInput 훅을 사용하여 파일 입력을 관리합니다.
  const { getInputProps } = useFileInput({
    onChange: (files) => {
      console.log(files); // 업로드된 파일 처리
    },
  });

  return (
    <div
      style={{
        border: '2px dashed #007bff',
        padding: '20px',
        textAlign: 'center',
        cursor: 'pointer',
      }}
      onClick={() => document.getElementById('file-input').click()} // 클릭 시 파일 선택
      onDragOver={(e) => e.preventDefault()} // 드래그 오버 시 기본 동작 방지
      onDrop={(e) => {
        e.preventDefault();
        const files = e.dataTransfer.files; // 드래그한 파일 가져오기
        if (files.length) {
          console.log(files); // 드롭한 파일 처리
        }
      }}
    >
      <input id="file-input" {...getInputProps()} style={{ display: 'none' }} />
      <p>파일을 드래그하거나 클릭하여 업로드하세요.</p>
    </div>
  );
};

const App = () => {
  return (
    <Uploady>
      <MyUploadyDropzone />
    </Uploady>
  );
};

export default App;
