import React, { useState, useEffect } from "react";
import "../css/Page1.css";
import DropFileInput from "./drop-file-input/DropFileInput";
import ImgDrop from "./drop-file-input/ImgDrop";

function PlusPage({ onRemove, onDataChange, existingData }) {
    const [title, setTitle] = useState(existingData?.title || '');
    const [content, setContent] = useState(existingData?.content || '');
    const [director, setDirector] = useState(existingData?.director || '');
    const [actors, setActors] = useState(existingData?.actors || '');
    const [additionalInfo, setAdditionalInfo] = useState(existingData?.additionalInfo || '');

    // 데이터 변경 시 부모에게 전달
    useEffect(() => {
        if (typeof onDataChange === 'function') {
            onDataChange({
                title,
                content,
                director,
                actors,
                additionalInfo,
            });
        }
    }, [title, content, director, actors, additionalInfo, onDataChange]);

    // 기존 데이터가 변경될 때 상태 업데이트
    useEffect(() => {
        setTitle(existingData?.title || '');
        setContent(existingData?.content || '');
        setDirector(existingData?.director || '');
        setActors(existingData?.actors || '');
        setAdditionalInfo(existingData?.additionalInfo || '');
    }, [existingData]);

    return (
        <article className="plusPage"> 
        sss
            <p className="info">
                2. 작품정보 <span className="three">(최대 3개까지 출품가능합니다)</span>
                <div className="del" onClick={onRemove}>
                    <i className="xi-close-circle-o"></i>
                </div>
            </p>
            <ul className="box box1">
                <li className="first">작품제목<span className="red">*</span></li>
                <li className="second">
                    <input
                        type="text"
                        className="movieTitle"
                        placeholder="작품제목을 입력해주세요."
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </li>
            </ul>
            <ul className="box box4">
                <li className="first">작품내용<span className="red">*</span></li>
                <li className="second">
                    <textarea
                        placeholder="작품내용을 입력해주세요"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                    />
                </li>
            </ul>
            <ul className="box box1">
                <li className="first">감독명<span className="red">*</span></li>
                <li className="second">
                    <input
                        type="text"
                        className="movieTitle"
                        placeholder="감독 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)"
                        value={director}
                        onChange={(e) => setDirector(e.target.value)}
                    />
                </li>
            </ul>
            <ul className="box box1">
                <li className="first">배우명</li>
                <li className="second">
                    <input
                        type="text"
                        className="movieTitle"
                        placeholder="배우 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)."
                        value={actors}
                        onChange={(e) => setActors(e.target.value)}
                    />
                </li>
            </ul>
            <ul className="box box1">
                <li className="first">추가입력사항</li>
                <li className="second">
                    <input
                        type="text"
                        className="movieTitle"
                        placeholder="예) 촬영,편집 등"
                        value={additionalInfo}
                        onChange={(e) => setAdditionalInfo(e.target.value)}
                    />
                </li>
            </ul>
            <ul className="box drop">
                <li className="first">작품 영상 첨부<span className="red">*</span></li>
                <li className="drop"><DropFileInput /></li>
            </ul>
            <ul className="box drop">
                <li className="first">작품 썸네일 첨부</li>
                <li className="drop"><ImgDrop /></li>
            </ul>
            <p className="imgText">※ 썸네일이란, 영상을 클릭하기 전에 내용을 미리 보여주는 작은 대표 이미지입니다.</p>
        </article>
    );
}

export default PlusPage;
