import React, { useCallback } from 'react';
import { DndProvider, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const FileDropZone = () => {
  const [{ isOver, canDrop }, drop] = useDrop({
    accept: 'file',
    drop: (item, monitor) => {
      const files = monitor.getItem().files;
      console.log('드롭된 파일:', files); // 드롭된 파일을 처리
    },
    canDrop: (item) => item.files && item.files.length > 0,
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  });

  const backgroundColor = isOver
    ? (canDrop ? 'lightgreen' : 'lightcoral')
    : 'white';

  return (
    <div
      ref={drop}
      style={{
        border: '2px dashed #007bff',
        padding: '20px',
        textAlign: 'center',
        backgroundColor,
        cursor: 'pointer',
      }}
    >
      <p>{isOver ? (canDrop ? '파일을 놓으세요!' : '드롭할 수 없습니다.') : '여기에 파일을 드롭하세요.'}</p>
    </div>
  );
};

const App = () => {
  const handleDragStart = (e) => {
    const files = Array.from(e.dataTransfer.files);
    e.dataTransfer.setData('application/json', JSON.stringify({ files })); // 드래그할 파일 데이터 설정
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <h1>파일 드롭 예시</h1>
        <FileDropZone />
        <input type="file" multiple onDragStart={handleDragStart} style={{ display: 'none' }} />
      </div>
    </DndProvider>
  );
};

export default App;
