import React from 'react';
import { DndProvider, useDrop, useDrag } from 'react-dnd'; // useDrag 추가
import { HTML5Backend } from 'react-dnd-html5-backend';

const FileDropZone = () => {
  const [{ isOver, canDrop }, drop] = useDrop({
    accept: 'file',
    drop: (item, monitor) => {
      const files = monitor.getItem().files;
      console.log('드롭된 파일:', files);
      // 여기에 드롭된 파일 처리 로직 추가
    },
    canDrop: (item) => item.files && item.files.length > 0,
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  });

  const backgroundColor = isOver
    ? (canDrop ? 'lightgreen' : 'lightcoral')
    : 'white';

  return (
    <div
      ref={drop}
      style={{
        border: '2px dashed #007bff',
        padding: '20px',
        textAlign: 'center',
        backgroundColor,
        cursor: 'pointer',
      }}
    >
      <p>{isOver ? (canDrop ? '파일을 놓으세요!' : '드롭할 수 없습니다.') : '여기에 파일을 드롭하세요.'}</p>
    </div>
  );
};

const FileDraggable = ({ file }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'file',
    item: { files: [file] },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      style={{
        padding: '10px',
        margin: '5px',
        border: '1px solid #007bff',
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
      }}
    >
      {file.name}
    </div>
  );
};

const App = () => {
  const [selectedFiles, setSelectedFiles] = React.useState([]);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <h1>파일 드래그 및 드롭 예시</h1>
        <input type="file" multiple onChange={handleFileChange} />
        <div style={{ margin: '20px 0' }}>
          {selectedFiles.map((file, index) => (
            <FileDraggable key={index} file={file} />
          ))}
        </div>
        <FileDropZone />
      </div>
    </DndProvider>
  );
};

export default App;
