import React, { createContext, useContext, useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import axios from 'axios';

const UserContext = createContext();

export const UserProvider = ({ children }) => {
  const [userInfo, setUserInfo] = useState(null);
  const [drawings, setDrawings] = useState([]);
  const [groupedDrawings, setGroupedDrawings] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [cookies] = useCookies(['accessToken']);
	const Server_IP = process.env.REACT_APP_Server_IP;

  const fetchUserInfo = async () => {
    const accessToken = cookies.accessToken;
    if (!accessToken) {
      console.error('No access token found');
      return;
    }
  
    try {
      const response = await axios.get(`${Server_IP}/member/me`, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        },
        withCredentials: true
      });
      console.log("Fetched User Info:", response.data); // API 응답 로그
      setUserInfo(response.data);
    } catch (error) {
      console.error('Error fetching user info:', error.response ? error.response.data : error.message);
    }
  };
  

  const fetchDrawings = async () => {
    const accessToken = cookies.accessToken;
    if (!accessToken) {
      console.error('No access token found');
      return;
    }
  
    try {
      const response = await axios.post(`${Server_IP}/drawing/get`, null, {
        headers: {
          'Authorization': `Bearer ${accessToken}`
        },
        withCredentials: true
      });
  
      if (Array.isArray(response.data)) {
        const drawings = response.data.map(drawing => {
          // Assume uploadDate is already in 'yyyy-MM-dd' format
          const formattedDate = drawing.uploadDate || 'Unknown Date';
  
          return {
            imgUrl: drawing.imgUrl || '',
            origin: drawing.origin || 'No Title',
            createdDate: formattedDate
          };
        });
        setDrawings(drawings);
        groupDrawingsByDate(drawings);
      } else {
        console.error('Unexpected data format:', response.data);
      }
    } catch (error) {
      console.error('Error fetching drawings:', error.response ? error.response.data : error.message);
    }
  };
  
  const groupDrawingsByDate = (drawings) => {
    const grouped = drawings.reduce((acc, drawing) => {
      const date = drawing.createdDate;
      if (!acc[date]) {
        acc[date] = [];
      }
      acc[date].push(drawing);
      return acc;
    }, {});
    setGroupedDrawings(grouped);
  };

  useEffect(() => {
    fetchUserInfo();
    fetchDrawings(); // Fetch drawings and group them when the token changes
  }, [cookies.accessToken]);

  return (
    <UserContext.Provider value={{ userInfo, fetchUserInfo, drawings, groupedDrawings, selectedDate, setSelectedDate }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => useContext(UserContext);
