import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { works } = location.state || { works: [] };

    const [videoURLs, setVideoURLs] = useState([]);
    const [thumbnailURLs, setThumbnailURLs] = useState([]);

    useEffect(() => {
        // 비디오 URL 생성
        const urls = works.map(work => work.videoFile instanceof File ? URL.createObjectURL(work.videoFile) : null);
        // 썸네일 URL 생성
        const thumbnailUrls = works.map(work => work.thumbnail instanceof File ? URL.createObjectURL(work.thumbnail) : null);

        setVideoURLs(urls);
        setThumbnailURLs(thumbnailUrls);

        // Clean up function
        return () => {
            urls.forEach(url => url && URL.revokeObjectURL(url));
            thumbnailUrls.forEach(url => url && URL.revokeObjectURL(url));
        };
    }, [works]);

    return (
        <div>
            <h1>작품 미리보기</h1>
            {works.map((work, index) => (
                <div key={index}>
                    <h2>{work.title}</h2>
                    <p>{work.description}</p>
                    <p>감독: {work.director}</p>
                    <p>배우: {work.actors}</p>
                    <p>추가 정보: {work.additionalInfo}</p>
                    
                    {/* 비디오 미리보기 */}
                    {videoURLs[index] ? (
                        <video controls>
                            <source src={videoURLs[index]} />
                            Your browser does not support the video tag.
                        </video>
                    ) : (
                        <p>비디오 파일이 없습니다.</p>
                    )}

                    {/* 썸네일 미리보기 */}
                    {thumbnailURLs[index] ? (
                        <img src={thumbnailURLs[index]} alt="Thumbnail" />
                    ) : (
                        <p>썸네일 파일이 없습니다.</p>
                    )}
                </div>
            ))}
        </div>
    );
}

export default Page2;
