import React from 'react';
import { FileDrop } from 'react-file-drop';

const MyFileDrop = () => {
  const handleDrop = (files) => {
    console.log(files);
  };

  return (
    <FileDrop onDrop={handleDrop} style={{ border: '2px dashed #007bff', padding: '20px', textAlign: 'center' }}>
      <p>여기에 파일을 드롭하세요.</p>
    </FileDrop>
  );
};

export default MyFileDrop;
