import React, { useState } from 'react';
import Header from "./Header";
import "./css/Page1.css";
import circle1 from '../src/image/page1.png';
import DropFileInput from "./components/drop-file-input/DropFileInput";
import ImgDrop from "./components/drop-file-input/ImgDrop";
import plus from "./image/plus.png";
import PlusPage from './components/PlusPage';
import next from "./image/right.png";
import { Link } from 'react-router-dom';

function Page1({ onFormDataChange }) {
    const [plusPages, setPlusPages] = useState([]); 
    const [formData, setFormData] = useState({
        name: '',
        id: '',
        contact: '',
        title: '',
        content: '',
        director: '',
        actors: '',
        additionalInfo: ''
    });

    const handleRemovePlusPage = (index) => {
        setPlusPages(plusPages.filter((_, i) => i !== index));
    };

    const handleAddPlusPage = () => {
        if (plusPages.length < 2) {
            setPlusPages([...plusPages, {
                title: '',
                content: '',
                director: '',
                actors: '',
                additionalInfo: '',
            }]);
        }
    };
    
    // PlusPage에 onDataChange prop 전달
    {plusPages.map((page, index) => (
        <PlusPage key={index} onRemove={() => handleRemovePlusPage(index)} onDataChange={(data) => {
            const updatedPages = [...plusPages];
            updatedPages[index] = data; // 현재 페이지 데이터 업데이트
            setPlusPages(updatedPages); // 상태 업데이트
        }} />
    ))}

    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = {
            ...formData,
            [name]: value
        };
        setFormData(updatedData); // 상태 업데이트
        onFormDataChange(updatedData); // 부모 컴포넌트로 데이터 전송
    };

    const handlePlusPageChange = (index, updatedPageData) => {
        const updatedPlusPages = [...plusPages];
        updatedPlusPages[index] = updatedPageData;
        setPlusPages(updatedPlusPages);
    };

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle1} className="circle1" alt="circle1" />
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr />
            <section>
                <p className="info">1.출품자 정보</p>
                <ul className="box box1">
                    <li className="first">성함<span className="red">*</span></li>
                    <li className="second"><input type="text" name="name" onChange={handleChange} placeholder="POBA누리 연동 자동 입력" /></li>
                </ul>
                <ul className="box box2">
                    <li className="first">아이디<span className="red">*</span></li>
                    <li className="second"><input type="text" name="id" onChange={handleChange} placeholder="POBA누리 연동 자동 입력" /></li>
                </ul>
                <ul className="box box3">
                    <li className="first">연락처<span className="red">*</span></li>
                    <li className="second"><input type="text" name="contact" onChange={handleChange} placeholder="POBA누리 연동 자동 입력" /></li>
                </ul>

                <p className="info">2.작품정보 <span className="three">(최대 2개까지 출품가능합니다)</span></p>
                <ul className="box box1">
                    <li className="first">작품제목<span className="red">*</span></li>
                    <li className="second"><input type="text" name="title" onChange={handleChange} className="movieTitle" placeholder="작품제목을 입력해주세요." /></li>
                </ul>
                <ul className="box box4">
                    <li className="first">작품내용<span className="red">*</span></li>
                    <li className="second"><textarea name="content" onChange={handleChange} placeholder="작품내용을 입력해주세요"></textarea></li>
                </ul>
                <ul className="box box1">
                    <li className="first">감독명<span className="red">*</span></li>
                    <li className="second"><input type="text" name="director" onChange={handleChange} className="movieTitle" placeholder="감독 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)" /></li>
                </ul>
                <ul className="box box1">
                    <li className="first">배우명</li>
                    <li className="second"><input type="text" name="actors" onChange={handleChange} className="movieTitle" placeholder="배우 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)." /></li>
                </ul>
                <ul className="box box1">
                    <li className="first">추가입력사항</li>
                    <li className="second"><input type="text" name="additionalInfo" onChange={handleChange} className="movieTitle" placeholder="예) 촬영,편집 등" /></li>
                </ul>
                <ul className="box drop">
                    <li className="first">작품 영상 첨부<span className="red">*</span></li>
                    <li className="drop"><DropFileInput /></li>
                </ul>
                <ul className="box drop">
                    <li className="first">작품 썸네일 첨부</li>
                    <li className="drop"><ImgDrop /></li>
                </ul>
                <p className="imgText">※ 썸네일이란, 영상을 클릭하기 전에 내용을 미리 보여주는 작은 대표 이미지입니다.</p>

                {/* 추가된 작품 정보 표시 */}
                {plusPages.map((page, index) => (
                    <PlusPage
                        key={index}
                        index={index}
                        onChange={(updatedPageData) => handlePlusPageChange(index, updatedPageData)}
                        onRemove={() => handleRemovePlusPage(index)}
                    />
                ))}
            </section>

            {plusPages.length < 2 && (
                <button className="plus" onClick={handleAddPlusPage}>
                    <img src={plus} alt="plus" />작품 추가하기
                </button>
            )}
            <Link to={{ pathname: "/page2", state: { formData, plusPages } }}>
                <div className='next'>
                    다음 <img src={next} alt="next" />
                </div>
            </Link>
        </div>
    );
}

export default Page1;
