import React, { useRef, useState } from 'react';
import PropTypes from 'prop-types';
import './drop-file-input.css';
import upload from "../../image/upload.png";

const DropFileInput = (props) => {
    const wrapperRef = useRef(null);
    const [file, setFile] = useState(null); // 파일을 단일 상태로 변경

    const onDragEnter = () => wrapperRef.current.classList.add('dragover');
    const onDragLeave = () => wrapperRef.current.classList.remove('dragover');
    const onDrop = () => wrapperRef.current.classList.remove('dragover');

    const onFileDrop = (e) => {
        const newFile = e.target.files[0]; // 첫 번째 파일만 가져옴
        if (newFile) {
            if (file) {
                alert('하나 이상의 파일을 업로드할 수 없습니다.'); // alert 창으로 경고 메시지
                return; // 이미 파일이 선택된 경우 종료
            }

            setFile(newFile); // 파일 상태 업데이트
            if (typeof props.onFileChange === 'function') {
                props.onFileChange(newFile); // 부모 컴포넌트에 파일 전달
            }

            // 비디오 파일을 서버에 업로드하는 로직
            const formData = new FormData();
            formData.append('file', newFile);

            fetch('http://localhost:3002/upload', {  // 여기에 실제 업로드 URL을 입력하세요
                method: 'POST',
                body: formData,
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error('파일 업로드 실패');
                }
                return response.json();
            })
            .then(data => {
                console.log('업로드 성공:', data);
                alert('업로드 성공: ' + data.message); // 성공 메시지
            })
            .catch(error => {
                console.error('업로드 오류:', error);
                alert('업로드 실패: ' + error.message); // 실패 메시지
            });
        }
    };

    const fileRemove = () => {
        setFile(null); // 파일 삭제
        if (typeof props.onFileChange === 'function') {
            props.onFileChange(null); // 부모 컴포넌트에 null 전달
        }
    };

    // 파일 크기를 KB 단위로 변환하는 함수
    const formatFileSize = (sizeInBytes) => {
        return (sizeInBytes / 1024).toFixed(2) + ' KB'; // 소수점 두 자리까지 표시
    };

    return (
        <>
            <div
                ref={wrapperRef}
                className="drop-file-input"
                onDragEnter={onDragEnter}
                onDragLeave={onDragLeave}
                onDrop={onDrop}
            >
                <div className="drop-file-input__label">
                    <img src={upload} className='upload' alt="Upload" /><br />
                    <b className='file-b'>파일을 여기로 드래그 하세요</b><br />
                    <p className='drop-smalltext file-b'>
                        영상 업로드 시 영상 크기에 따라 10~60초 가량 소모됩니다.<br />
                        ※ 모든 형식의 비디오를 지원합니다.
                    </p>
                </div>
                <input type="file" onChange={onFileDrop} />
            </div>
            {file && ( // 파일이 있을 경우에만 미리보기 표시
                <div className="drop-file-preview">
                    <div className="drop-file-preview__item">
                        <div className="drop-file-preview__item__info">
                            <p>{file.name}</p>
                            <p>{formatFileSize(file.size)}</p> {/* KB 단위로 표시 */}
                        </div>
                        <span className="drop-file-preview__item__del" onClick={fileRemove}>x</span>
                    </div>
                </div>
            )}
        </>
    );
};

// PropTypes 설정
DropFileInput.propTypes = {
    onFileChange: PropTypes.func.isRequired, // 필수 prop으로 설정
};

// 기본 props 설정
DropFileInput.defaultProps = {
    onFileChange: () => {}, // 기본적으로 빈 함수
};

export default DropFileInput;
