import React from 'react';
import { FileDrop } from 'react-file-drop';

const MyFileDrop = () => {
  const handleDrop = (files) => {
    console.log('드롭된 파일:', files);
  };

  return (
    <FileDrop 
      onDrop={handleDrop} 
      onTargetClick={() => console.log("드롭존 클릭됨")} // 드롭존 클릭 시 로그
      onDropReady={() => console.log("드롭 준비 완료")} // 드롭 준비 완료 시 로그
      style={{ 
        border: '2px dashed #007bff', 
        padding: '20px', 
        textAlign: 'center', 
        cursor: 'pointer' 
      }}
    >
      <p>여기에 파일을 드롭하세요.</p>
    </FileDrop>
  );
};

const App = () => {
  return (
    <div>
      <h1>파일 드롭 예시</h1>
      <MyFileDrop />
    </div>
  );
};

export default App;
