import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import Page1 from './Page1';
import Page2 from './Page2';

const App = () => {
  const [formData, setFormData] = useState({}); // 상태 관리
  const [plusPages, setPlusPages] = useState([]); // 추가된 작품 정보 관리

  const onFormDataChange = (data) => {
    setFormData(data); // 데이터 업데이트
  };

  const onPlusPagesChange = (pages) => {
    setPlusPages(pages); // 추가 페이지 데이터 업데이트
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Page1 onFormDataChange={onFormDataChange} onPlusPagesChange={onPlusPagesChange} />} />
        <Route path="/page2" element={<Page2 formData={formData} plusPages={plusPages} />} />
      </Routes>
    </Router>
  );
};

export default App;
