import React, { useState } from 'react';
import Header from "./Header";
import "./css/Page1.css";
import circle1 from '../src/image/page1.png';
import DropFileInput from "./components/drop-file-input/DropFileInput";
import ImgDrop from "./components/drop-file-input/ImgDrop";
import plus from "./image/plus.png";
import PlusPage from './components/PlusPage';
import next from "./image/right.png";
import { Link } from 'react-router-dom';

function Page1() {
    const [formData, setFormData] = useState({
        name: '',
        id: '',
        contact: ''
    });

    const [works, setWorks] = useState([{
        title: '',
        description: '',
        director: '',
        actors: '',
        additionalInfo: ''
    }]);

    // PlusPage 삭제 함수
    const handleRemovePlusPage = (index) => {
        setWorks(works.filter((_, i) => i !== index));
    };

    // PlusPage 추가 함수
    const handleAddPlusPage = () => {
        if (works.length < 3) { // 최대 3개까지 추가
            setWorks([...works, { title: '', description: '', director: '', actors: '', additionalInfo: '' }]);
        }
    };

    // 작품 정보 변경 함수
    const handleWorkChange = (index, field, value) => {
        const newWorks = [...works];
        newWorks[index][field] = value;
        setWorks(newWorks);
    };

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle1} className="circle1" alt="circle1"></img>
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr />
            <section>
                <p className="info">1.출품자 정보</p>
                <ul className="box box1">
                    <li className="first">성함<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="POBA누리 연동 자동 입력" 
                            value={formData.name}
                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                        />
                    </li>
                </ul>
                <ul className="box box2">
                    <li className="first">아이디<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="POBA누리 연동 자동 입력" 
                            value={formData.id}
                            onChange={(e) => setFormData({ ...formData, id: e.target.value })}
                        />
                    </li>
                </ul>
                <ul className="box box3">
                    <li className="first">연락처<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="POBA누리 연동 자동 입력" 
                            value={formData.contact}
                            onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
                        />
                    </li>
                </ul>

                <p className="info">2.작품정보 <span className="three">(최대 3개까지 출품가능합니다)</span></p>

                {/* 작품 입력 필드 표시 */}
                {works.map((work, index) => (
                    <div key={index}>
                        <ul className="box box1">
                            <li className="first">작품제목<span className="red">*</span></li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="작품제목을 입력해주세요." 
                                    value={work.title}
                                    onChange={(e) => handleWorkChange(index, 'title', e.target.value)}
                                />
                            </li>
                        </ul>
                        <ul className="box box4">
                            <li className="first">작품내용<span className="red">*</span></li>
                            <li className="second">
                                <textarea 
                                    placeholder="작품내용을 입력해주세요"
                                    value={work.description}
                                    onChange={(e) => handleWorkChange(index, 'description', e.target.value)}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">감독명<span className="red">*</span></li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="감독 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)" 
                                    value={work.director}
                                    onChange={(e) => handleWorkChange(index, 'director', e.target.value)}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">배우명</li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="배우 이름을 입력해주세요 (2명 이상인 경우, 쉼표로 구분해주세요)." 
                                    value={work.actors}
                                    onChange={(e) => handleWorkChange(index, 'actors', e.target.value)}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">추가입력사항</li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="예) 촬영,편집 등" 
                                    value={work.additionalInfo}
                                    onChange={(e) => handleWorkChange(index, 'additionalInfo', e.target.value)}
                                />
                            </li>
                        </ul>
                    </div>
                ))}

                {/* 추가된 PlusPage 컴포넌트들 */}
                {works.length < 3 && (
                    <button className="plus" onClick={handleAddPlusPage}>
                        <img src={plus} alt="plus"></img>작품 추가하기
                    </button>
                )}
            </section>

            <Link 
                to={{
                    pathname: "/page2",
                    state: { 
                        formData, 
                        works // 작품 정보 배열
                    }
                }}
            >
                <div className='next'>
                    다음 <img src={next} alt="next"/>
                </div>
            </Link>
        </div>
    );
}

export default Page1;
