import React from 'react';
import Header from "./Header";
import "./css/Page2.css";
import circle2 from '../src/image/page2.png';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { formData = {}, plusPages = [] } = location.state || {}; // 기본값 설정

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle2} className="circle1" alt="circle1"></img>
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li className='red'>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr></hr>
            <section>
                <p className="info">출품정보 확인</p>
                <ul className="box box1">
                    <li className="first">작품제목</li>
                    <li className="second">{formData.title || '정보 없음'}</li>
                </ul>
                <ul className="box box4">
                    <li className="first">작품내용</li>
                    <li className="second">{formData.description || '정보 없음'}</li>
                </ul>
                <ul className="box box1">
                    <li className="first">감독명</li>
                    <li className="second">{formData.director || '정보 없음'}</li>
                </ul>
                <ul className="box box1">
                    <li className="first">배우명</li>
                    <li className="second">{formData.actors || '정보 없음'}</li>
                </ul>
                <ul className="box box1">
                    <li className="first">추가입력사항</li>
                    <li className="second">{formData.additional || '정보 없음'}</li>
                </ul>

                {/* 추가된 작품 정보 표시 */}
                {plusPages.length > 0 && (
                    <div>
                        <h3>추가된 작품들:</h3>
                        {plusPages.map((page, index) => (
                            <div key={index}>
                                {/* 여기에 PlusPage 컴포넌트의 정보를 보여주도록 구현 */}
                                {/* 예시: <p>작품 {index + 1} 정보</p> */}
                                <p>작품 {index + 1}: {page.props.someData || '정보 없음'}</p>
                            </div>
                        ))}
                    </div>
                )}
            </section>
        </div>
    );
}

export default Page2;
