import React from 'react';
import { Uploady, useFileInput } from '@rpldy/uploady';

const MyUploadyDropzone = () => {
  // useFileInput 훅을 통해 드롭존 및 파일 입력을 관리합니다.
  const { getInputProps, getRootProps } = useFileInput({
    onChange: (files) => {
      console.log(files); // 업로드된 파일 처리
    },
  });

  return (
    <div
      {...getRootProps()}
      style={{
        border: '2px dashed #007bff',
        padding: '20px',
        textAlign: 'center',
        cursor: 'pointer',
      }}
    >
      <input {...getInputProps()} />
      <p>파일을 드래그하거나 클릭하여 업로드하세요.</p>
    </div>
  );
};

const App = () => {
  return (
    <Uploady>
      <MyUploadyDropzone />
    </Uploady>
  );
};

export default App;
