import React from 'react';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { works } = location.state || { works: [] }; // Page1에서 전달받은 작품 정보

    return (
        <div>
            <h1>작품 미리보기</h1>
            <div>
                {works.map((work, index) => (
                    <div key={index}>
                        <h2>{work.title}</h2>
                        <p>{work.description}</p>
                        <p>감독: {work.director}</p>
                        <p>배우: {work.actors}</p>
                        <p>추가 정보: {work.additionalInfo}</p>
                        
                        {/* 비디오 미리보기 */}
                        {work.videoFile && work.videoFile instanceof File && (
                            <video controls width="300">
                                <source src={URL.createObjectURL(work.videoFile)} type="video/mp4" />
                                Your browser does not support the video tag.
                            </video>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Page2;
