import React from 'react';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { formData, works } = location.state || { formData: {}, works: [] }; // 초기값 설정

    return (
        <div>
            <h1>출품자 정보</h1>
            <p>이름: {formData.name}</p>
            <p>아이디: {formData.id}</p>
            <p>연락처: {formData.contact}</p>

            <h1>작품 정보</h1>
            {works.map((work, index) => (
                <div key={index}>
                    <h2>{work.title}</h2>
                    <p>감독: {work.director}</p>
                    <p>내용: {work.description}</p>
                    <p>배우: {work.actors}</p>
                    <p>추가정보: {work.additionalInfo}</p>
                    {/* 비디오 및 썸네일 파일 정보 출력 */}
                    {work.videoFile && <p>비디오 파일: {work.videoFile.name}</p>}
                    {work.thumbnailFile && <p>썸네일 파일: {work.thumbnailFile.name}</p>}
                </div>
            ))}
        </div>
    );
}

export default Page2;
