import React, { useState } from 'react';
import Header from "./Header";
import "./css/Page1.css";
import circle1 from '../src/image/page1.png';
import DropFileInput from "./components/drop-file-input/DropFileInput";
import ImgDrop from "./components/drop-file-input/ImgDrop";
import plus from "./image/plus.png";
import next from "./image/right.png";
import { Link } from 'react-router-dom';

function Page1({ formData, updateFormData, works, updateWorkInfo, handleAddPlusPage }) {
    const [workFiles, setWorkFiles] = useState(works.map(() => ({ videoUrl: '', thumbnailUrl: '' })));

    // 파일 선택 핸들러
    const handleFileChange = (index, fileType, file) => {
        const updatedWorks = [...works];
        const updatedWorkFiles = [...workFiles];
        if (fileType === 'video') {
            updatedWorks[index].videoFile = file;
            updatedWorkFiles[index].videoUrl = URL.createObjectURL(file); // 동영상 URL 생성
        } else if (fileType === 'thumbnail') {
            updatedWorks[index].thumbnailFile = file;
            updatedWorkFiles[index].thumbnailUrl = URL.createObjectURL(file); // 썸네일 URL 생성
        }
        setWorkFiles(updatedWorkFiles);
        updateWorkInfo(index, updatedWorks[index]); // 업데이트된 작품 정보를 상위 상태로 전달
    };

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle1} className="circle1" alt="circle1" />
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr />
            <section>
                <p className="info">1. 출품자 정보</p>
                {/* 출품자 정보 입력란 */}
                <ul className="box box1">
                    <li className="first">성함<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="성함을 입력해주세요." 
                            value={formData.name}
                            onChange={(e) => updateFormData('name', e.target.value)} 
                        />
                    </li>
                </ul>
                <ul className="box box2">
                    <li className="first">아이디<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="아이디를 입력해주세요." 
                            value={formData.id}
                            onChange={(e) => updateFormData('id', e.target.value)} 
                        />
                    </li>
                </ul>
                <ul className="box box3">
                    <li className="first">연락처<span className="red">*</span></li>
                    <li className="second">
                        <input 
                            type="text" 
                            placeholder="연락처를 입력해주세요." 
                            value={formData.contact}
                            onChange={(e) => updateFormData('contact', e.target.value)} 
                        />
                    </li>
                </ul>

                <p className="info">2. 작품정보 <span className="three">(최대 3개까지 출품가능합니다)</span></p>

                {/* 작품 정보 입력란 */}
                {works.map((work, index) => (
                    <div key={index}>
                        <ul className="box box1">
                            <li className="first">작품제목<span className="red">*</span></li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="작품제목을 입력해주세요."
                                    value={work.title}
                                    onChange={(e) => updateWorkInfo(index, { title: e.target.value })}
                                />
                            </li>
                        </ul>
                        <ul className="box box4">
                            <li className="first">작품내용<span className="red">*</span></li>
                            <li className="second">
                                <textarea 
                                    placeholder="작품내용을 입력해주세요"
                                    value={work.description}
                                    onChange={(e) => updateWorkInfo(index, { description: e.target.value })}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">감독명<span className="red">*</span></li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="감독 이름을 입력해주세요."
                                    value={work.director}
                                    onChange={(e) => updateWorkInfo(index, { director: e.target.value })}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">배우명</li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="배우 이름을 입력해주세요."
                                    value={work.actors}
                                    onChange={(e) => updateWorkInfo(index, { actors: e.target.value })}
                                />
                            </li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">추가입력사항</li>
                            <li className="second">
                                <input 
                                    type="text" 
                                    className="movieTitle" 
                                    placeholder="예) 촬영,편집 등"
                                    value={work.additionalInfo}
                                    onChange={(e) => updateWorkInfo(index, { additionalInfo: e.target.value })}
                                />
                            </li>
                        </ul>
                        <ul className="box drop">
                            <li className="first">작품 영상 첨부<span className="red">*</span></li>
                            <li className="drop">
                                <DropFileInput onFileChange={(file) => handleFileChange(index, 'video', file)} />
                            </li>
                        </ul>
                        <ul className="box drop">
                            <li className="first">작품 썸네일 첨부</li>
                            <li className="drop">
                                <ImgDrop onFileChange={(file) => handleFileChange(index, 'thumbnail', file)} />
                            </li>
                        </ul>
                    </div>
                ))}

                {/* 작품 추가 버튼 */}
                {works.length < 3 && (
                    <button className="plus" onClick={handleAddPlusPage}>
                        <img src={plus} alt="plus"></img>작품 추가하기
                    </button>
                )}
            </section>

            {/* 다음 페이지로 이동하는 버튼 */}
            <Link 
                to={{
                    pathname: "/page2",
                    state: { formData, works, workFiles } // 출품자 정보, 작품 정보 및 파일 URL 전달
                }}
            >
                <div className='next'>
                    다음 <img src={next} alt="next"/>
                </div>
            </Link>
        </div>
    );
}

export default Page1;
