import React from 'react';
import { Uploady, useFileInput } from '@rpldy/uploady';

const App = () => {
  const { getInputProps, getRootProps } = useFileInput({
    onChange: (files) => {
      console.log(files); // 업로드된 파일 처리
    },
  });

  return (
    <Uploady>
      <div
        {...getRootProps()}
        style={{
          border: '2px dashed #007bff',
          padding: '20px',
          textAlign: 'center',
          cursor: 'pointer',
        }}
      >ss
        <input {...getInputProps()} />
        <p>파일을 드래그하거나 클릭하여 업로드하세요.</p>
      </div>
    </Uploady>
  );
};

export default App;
