import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Page1 from './Page1';
import Page2 from './Page2';

function App() {
    const [formData, setFormData] = useState({ name: '', id: '', contact: '' });
    const [works, setWorks] = useState([{}]); // 초기값으로 하나의 작품만 포함

    // 작품 정보 업데이트 함수
    const updateWorkInfo = (index, data) => {
        const updatedWorks = [...works];
        updatedWorks[index] = { ...updatedWorks[index], ...data };
        setWorks(updatedWorks);
    };

    return (
        <Router>
            <Switch>
                <Route path="/page1">
                    <Page1 formData={formData} setFormData={setFormData} works={works} updateWorkInfo={updateWorkInfo} />
                </Route>
                <Route path="/page2">
                    <Page2 formData={formData} works={works} />
                </Route>
                {/* 다른 라우트 추가 */}
            </Switch>
        </Router>
    );
}

export default App;
