import React from 'react';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { works } = location.state || { works: [] }; // works를 가져옵니다

    return (
        <div>
            <h1>작품 미리보기</h1>
            {works.map((work, index) => (
                <div key={index}>
                    <h2>{work.title}</h2>
                    <p>{work.description}</p>
                    <p>감독: {work.director}</p>
                    <p>배우: {work.actors}</p>
                    <p>추가 정보: {work.additionalInfo}</p>
                    
                    {/* 비디오 미리보기 */}
                    {work.videoFile && (
                        <video controls>
                            <source src={URL.createObjectURL(work.videoFile)} />
                            Your browser does not support the video tag.
                        </video>
                    )}

                    {/* 썸네일 미리보기 */}
                    {work.thumbnail && (
                        <img src={URL.createObjectURL(work.thumbnail)} alt="Thumbnail" />
                    )}
                </div>
            ))}
        </div>
    );
}

export default Page2;
