import React from 'react';
import { useLocation, Navigate } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { formData, works, workFiles } = location.state || {};

    if (!formData || !works || !workFiles) {
        return <Navigate to="/" />; // 기본 페이지로 리다이렉션
    }

    return (
        <div>
            <h1>출품자 정보</h1>
            <p>이름: {formData.name}</p>
            <p>아이디: {formData.id}</p>
            <p>연락처: {formData.contact}</p>

            <h1>작품 정보</h1>
            {works.map((work, index) => (
                <div key={index}>
                    <h2>{work.title}</h2>
                    <p>감독: {work.director}</p>
                    <p>내용: {work.description}</p>
                    <p>배우: {work.actors}</p>
                    <p>추가정보: {work.additionalInfo}</p>
                    {workFiles[index]?.videoUrl && <p>비디오 파일: <a href={workFiles[index].videoUrl} target="_blank" rel="noopener noreferrer">비디오 보기</a></p>}
                    {workFiles[index]?.thumbnailUrl && <p>썸네일 파일: <img src={workFiles[index].thumbnailUrl} alt="Thumbnail" width="100" /></p>}
                </div>
            ))}
        </div>
    );
}

export default Page2;
