import React from "react";
import Header from "./Header";
import "./css/Page2.css";
import circle2 from '../src/image/page2.png';
import { useLocation } from 'react-router-dom';

function Page2() {
    const location = useLocation();
    const { works = [] } = location.state || {}; // 기본값 설정

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle2} className="circle1" alt="circle1" />
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li className='red'>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr />
            <section>
                <p className="info">출품정보 확인</p>

                {/* 출품자 정보 표시 */}
                {/* ... (여기에는 출품자 정보 표시 코드가 들어갑니다) ... */}

                <h3>작품 정보:</h3>

                {/* 작품 정보 표시 */}
                {works.map((work, index) => (
                    <div key={index}>
                        <ul className="box box1">
                            <li className="first">작품제목</li>
                            <li className="second">{work.title || '정보 없음'}</li>
                        </ul>
                        <ul className="box box4">
                            <li className="first">작품내용</li>
                            <li className="second">{work.description || '정보 없음'}</li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">감독명</li>
                            <li className="second">{work.director || '정보 없음'}</li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">배우명</li>
                            <li className="second">{work.actors || '정보 없음'}</li>
                        </ul>
                        <ul className="box box1">
                            <li className="first">추가입력사항</li>
                            <li className="second">{work.additionalInfo || '정보 없음'}</li>
                        </ul>

                        {/* 썸네일과 동영상 표시 */}
                        {work.thumbnailFile && (
                            <div>
                                <h4>썸네일:</h4>
                                <img 
                                    src={URL.createObjectURL(work.thumbnailFile)} 
                                    alt="Thumbnail" 
                                    style={{ cursor: 'pointer' }} 
                                    onClick={() => {
                                        const videoUrl = URL.createObjectURL(work.videoFile);
                                        const videoWindow = window.open(videoUrl, '_blank');
                                        videoWindow.focus();
                                    }}
                                />
                            </div>
                        )}
                    </div>
                ))}
            </section>
        </div>
    );
}

export default Page2;
