// Page2.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import Header from "./Header";
import "./css/Page1.css";
import "./css/Page2.css";
import circle2 from '../src/image/page2.png';

function Page2() {
    const location = useLocation();
    const { works } = location.state || { works: [] }; // Page1에서 전달된 작품 정보 가져오기

    return (
        <div>
            <Header />
            <div className="circlezone">
                <img src={circle2} className="circle1" alt="circle1" />
            </div>
            <ul className="circleText">
                <li>01. 정보 입력</li>
                <li className='red'>02. 동의 및 정보 확인</li>
                <li>03. 출품 완료</li>
            </ul>
            <hr />
            <section>
                {/* 유의사항 및 동의 내용 생략 */}
                
                <p className="info">출품정보 확인</p>
                {works.map((work, index) => (
                    <div key={index}>
                        <ul className="box box1">
                            <li className="first">작품제목<span className="red">*</span></li>
                            <li className="second">{work.title}</li>
                        </ul>
                        {/* 기타 정보 표시 생략 */}
                        <ul className="box drop">
                            <li className="first">작품 영상 첨부</li>
                            <li className="drop">
                                <div>
                                    <span>영상 이름: {work.videoName || '업로드되지 않음'}</span><br />
                                    <span>크기: {work.videoSize ? `${(work.videoSize / 1024).toFixed(2)} KB` : '0 KB'}</span><br />
                                    <span>시간: {work.videoDuration || '0:00'}</span>
                                    {work.videoUrl && (
                                        <video width="320" height="240" controls>
                                            <source src={work.videoUrl} type="video/mp4" />
                                            브라우저가 비디오 태그를 지원하지 않습니다.
                                        </video>
                                    )}
                                </div>
                            </li>
                        </ul>
                        {/* 썸네일 및 기타 정보 표시 생략 */}
                    </div>
                ))}
            </section>
        </div>
    );
}

export default Page2;
